        wxUserDeviceId: this.deviceId,
        openId: this.userInfo.openid
      }).then(function (res) {
        if (res.code == 10000) {
          Toast('允许成功');
          _this2.loading = false;
          _this2.disabledT = true;
          _this2.disabledW = true;
        } else {
          _this2.loading = false;
          Toast(res.msg ? res.msg : '允许失败');
        }
      }).catch(function (e) {
        _this2.loading = false;
        Toast('允许失败');
        console.log(e);
      });
