import { Actionsheet, Loading, Toast, Dialog } from 'vant';
import ItemList from '../components/citemlist';
import { get, post } from '../../src/util/http.js';
import { formatDatePub, nyr } from '../../src/util/util.js';

require('../../src/util/cookie.js');

export default {
  name: 'home',
  components: {
    Loading: Loading,
    Actionsheet: Actionsheet,
    Toast: Toast,
    Dialog: Dialog,
    ItemList: ItemList
  },
  filters: {
    nyr: nyr
  },
  methods: {
    onSelects: function onSelects() {
      this.getList();
    },
    onSelect: function onSelect(item) {
      this.show = false; // console.log(item)/

      if (item.url == 'first') {
        if (item.type == 'gotop') {
          var opt = this.items[this.currentIndex];
          this.top(opt);
        } else if (item.type == 'cancelgotop') {
          var _opt = this.items[this.currentIndex];
          this.top(_opt);
        }

        return;
      } else if (item.url == 'delete') {
        var _opt2 = this.items[this.currentIndex];
        this.dealer(_opt2);
        return;
      } else if (item.url == '/wsd/') {
        var items = this.items[this.currentIndex];
        var _opt3 = {
          path: item.url + '' + items.device_id
        };
        this.selectId = '';
        this.$router.push(_opt3);
      } else {
        var _opt4 = {
          path: item.url + '' + this.selectId
        };
        this.selectId = '';
        this.$router.push(_opt4);
      }
    },
    toPercent: function toPercent(point) {
      var str = Number(point * 100).toFixed(1);
      str += "%";
      return str;
    },
    getList: function getList() {
      var _this = this;

      var opendId = Cookies.get('openId');
      get('/device/list', {
        openId: opendId
      }).then(function (res) {
        if (res.code == 10000) {
          _this.loading = false;
          _this.items = res.data;

          _this.items.forEach(function (el, i) {
            el.probe_type = el.probe_type == 1 ? '内置探头' : '外置探头 '; // el.time=formatDatePub(el.report_parse_acquisition_time)

            if (el.report_parse_voltage > 3.6) {
              // 高
              el.powerL = 1;
            } else if (el.report_parse_voltage < 3.6 && el.report_parse_voltage > 3.5) {
              // 中
              el.powerL = 2;
            } else if (el.report_parse_voltage < 3.5 && el.report_parse_voltage > 3.45) {
              // 低
              el.powerL = 3;
            } else {
              el.powerL = 4;
            }
          });

          _this.items.sort(function (a, b) {
            if (b.top != undefined && a.top != undefined) {
              return b.top - a.top;
            }
          });
        }
      }).catch(function (e) {
        console.log(e);
      });
    },
    toSearch: function toSearch() {
      var opt = {
        path: '/search',
        query: {
          id: ''
        }
      };
      this.$router.push(opt);
    }
  },
  directives: {
    db: {
      inserted: function inserted(el, value) {
        el.innerText = value.value;
      }
    }
  },
  data: function data() {
    return {
      selectId: '',
      //当前选择的设备id
      show: false,
      isFirst: false,
      items: [],
      loading: false,
      actions: []
    };
  },
  mounted: function mounted() {
    var _this2 = this;

    console.log('------还是会触发我么？？？？？');
    this.loading = true; //oJ3fKwNf3IkVFiAGKVu2WE-0i918
    // var key='oJ3fKwNf3IkVFiAGKVu2WE-0i918'
    // Cookies('openId',key,{ expires: new Date(new Date().getTime() + (2*24*60*60 * 1000))})

    if (Cookies.get('openId')) {
      // 如果cookie  有openId
      this.getList();
    } else {
      var code = window.location.search.split('&')[0].split('=')[1];

      if (code) {
        // 用code  换openId
        get('/wx/getWebTokenByCode', {
          code: code
        }).then(function (res) {
          if (res.code == 10000 && res.data != undefined && res.data.openid != '' && res.data.openid != undefined) {
            _this2.userInfo = res.data;
            Cookies('openId', _this2.userInfo.openid, {
              expires: new Date(new Date().getTime() + 2 * 24 * 60 * 60 * 1000)
            });

            _this2.getList();
          } else {
            // code 失效 或者接口挂了
            window.location.href = 'https://open.weixin.qq.com/connect/oauth2/authorize?appid=wxf410780b57e7f0ad&redirect_uri=http%3a%2f%2flive-smart.com.cn%2findex.html%23%2f&response_type=code&scope=snsapi_base&state=STATE#wechat_redirect';
          }
        }).catch(function (e) {
          console.log(e);
        });
      } else {
        // 没有code 去这个地址
        window.location.href = 'https://open.weixin.qq.com/connect/oauth2/authorize?appid=wxf410780b57e7f0ad&redirect_uri=http%3a%2f%2flive-smart.com.cn%2findex.html%23%2f&response_type=code&scope=snsapi_base&state=STATE#wechat_redirect';
      }
    }
  }
};
