// @ is an alias to /src
import { Button,Loading,Toast,DatetimePicker } from 'vant';
import {get,post} from '../../src/util/http.js'

export default {
  name: 'wsd',
  components: {
    Button,
    Loading,
    Toast
  },
  computed:{
    classObject:function(){

    }
  },
  methods:{
    Onconfirm(value){
      let d=new Date(value)
      let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+d.getDate()+'').getTime()
      _t=_t/1000
      console.log(_t)
      this.selectDate=_t
      this.getdatas()
    },

    cut(item){
      this.currentTab=item
      if(item==1){ // 当前是否是今天的数据
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+d.getDate()+'').getTime()
        _t=_t/1000
        if(this.data && this.data.date==_t)
        return
      }

      if(item==2){
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate()-1)+'').getTime()
        _t=_t/1000
        if(this.data && this.data.date==_t)
        return
      }

      if(item==3){ // 当前是否是前天的数据
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate()-2)+'').getTime()
        _t=_t/1000
        if(this.data && this.data.date==_t)
        return
      }

      if(item==4){ // 当前是否是本月的数据
        let _t='1'
        if(this.data && this.data.date==_t)
        return
      }

      if(item==5){ // 当前是否是本月的数据
        let _t='2'
        if(this.data && this.data.date==_t)
        return
      }
      if(item==6){ // 当前是否是本月的数据
        let _t='3'
        if(this.data && this.data.date==_t)
        return
      }

      if(item==1){ // 今天
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+d.getDate()+'').getTime()
        _t=_t/1000
        this.selectDate=_t
        this.getdatas()
      }else if(item==2) { //昨天
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate()-1)+'').getTime()
        _t=_t/1000
        this.selectDate=_t
        this.getdatas()
      }else if(item==3){
        let d=new Date()
        let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate()-2)+'').getTime()
        _t=_t/1000
        this.selectDate=_t
        this.getdatas()
      }else if(item==4){
        this.selectDate='1'
        this.getdatas()
      }else if(item==5){
        this.selectDate='2'
        this.getdatas()
      }else if(item==6){
        this.selectDate='3'
        this.getdatas()
      }
    },

    Oncancel(){
      this.datePicker=false
    },

    onElse(){
      this.datePicker=true
    },
    onToday(){
      let d=new Date()
      let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+d.getDate()+'').getTime()
      _t=_t/1000
      this.selectDate=_t
      this.getdatas()
    },
    onYesterday(){
      let d=new Date()
      let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate()-1)+'').getTime()
      _t=_t/1000
      this.selectDate=_t
      this.getdatas()
    },


    hideDatePicker(){
      this.datePicker=false
    },
    init(){
      let d=new Date()
      let _t=new Date(''+d.getFullYear()+'/'+(d.getMonth()+1)+'/'+(d.getDate())+'').getTime()
      _t=_t/1000
      this.selectDate=_t
      this.getdatas()
    },

    getDataDate(){
      let d=new Date((this.data.date*1000))
      let s=d.getFullYear()+'-'+(d.getMonth()+1)+'-'+d.getDate()
      return s
    },

    drawLine(){
      let _this=this
      let myChart = this.$echarts.init(document.getElementById('wd'))
      let myChart2 = this.$echarts.init(document.getElementById('sd'))
      let myChart3 = this.$echarts.init(document.getElementById('power'))
      let option = {
          title: {
            text: '湿度曲线',
            subtext: '',
            x: 'center'
          },
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              animation: false
            }
          },
          xAxis: {
            type: 'category',
            data:_this.d1
          },
          yAxis: {
              type: 'value'
          },
          series: [{
              data: _this.d2,
              type: 'line'
          }],
          dataZoom: [{
            }, {
                type: 'inside'
            }],
      };
      let option2 = {
          title: {
            text: '温度曲线',
            subtext: '',
            x: 'center'
          },
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              animation: false
            }
          },
          xAxis: {
            type: 'category',
            data:_this.d1
          },
          yAxis: {
              type: 'value'
          },
          series: [{
              data: _this.d3,
              type: 'line'
          }],
          dataZoom: [{
            }, {
                type: 'inside'
            }],
      };
      let option3 = {
          title: {
            text: '电量',
            subtext: '',
            x: 'center'
          },
          tooltip: {
            trigger: 'axis',
            axisPointer: {
              animation: false
            }
          },
          xAxis: {
            type: 'category',
            data:_this.d1
          },
          yAxis: {
              type: 'value'
          },
          series: [{
              data: _this.d4,
              type: 'line'
          }],
          dataZoom: [{
            }, {
                type: 'inside'
            }],
      };
      // var obj={
      //   data:[_this.getDataDate()],
      //   x: 'left'
      // }
      // option.legend=obj
      console.log(option,'---------------')
      myChart.setOption(option)
      myChart2.setOption(option2)
      myChart3.setOption(option3)
      window.addEventListener("resize", () => { myChart.resize(); myChart2.resize(); myChart3.resize();});
    },
    getdatas(){
      var data={
        deviceId :this.deviceId
      }
      if(this.selectDate==='1' || this.selectDate==='2' || this.selectDate==='3'){
        data.flag=this.selectDate
      }else {
        data.date=this.selectDate
      }
      get('/device/reportData',data).then((res)=>{
        if(res.code==10000){
          if(res.data.humidity.length>0&& res.data.temperature.length>0&& res.data.voltage.length>0){
            this.data=res.data
            this.data.date=this.selectDate
            this.loading=false
            this.d1=res.data.timeData
            if(this.selectDate==='1' || this.selectDate==='2' || this.selectDate==='3'){
              let reg=/\d{4}-(\d{1,2}-\d{1,2})/
              let _d=[]
              this.d1.forEach(function(item,index){
                if(reg.test(item)){
                  _d.push(RegExp.$1)
                }
              })
              this.d1=_d
            }else {
              let reg=/\d{4}-\d{1,2}-\d{1,2}/
              this.d1 = this.d1.map(function (str) {
                return str.replace(reg, '');
              });
            }
            this.d2=res.data.humidity
            this.d3=res.data.temperature
            this.d4=res.data.voltage
            this.drawLine()
            this.hideDatePicker()
            this.loading=false
          }else {
            this.loading=false
            this.data=null
            this.d2=[]
            this.d3=[]
            this.d4=[]
            this.d1=[]
            this.drawLine()
            Toast('暂无数据...')
            this.hideDatePicker()
            this.loading=false
          }
        }else {
          this.loading=false
          Toast('获取数据失败，请稍后')
          this.hideDatePicker()
          this.loading=false
        }
      }).catch((e)=>{
        this.loading=false
        this.hideDatePicker()
        Toast('获取数据失败，请稍后')
        console.log(e)
      })
    }
  },
  data(){
    return {
      deviceId:'',
      selectDate:'',
      currentDate: new Date(),
      currentDateT: new Date(),
      minDate: new Date('2018/1/1'),
      datePicker:false,
      d1:[],
      d2:[],
      d3:[],
      d4:[],
      currentTab:true,
      data:null,
      loading:true
    }
  },
  mounted(){
    this.deviceId=this.$route.params.id
    this.init()

  }
}
